let container;function getContainer() {if (container) return container;container = document.getElementById('toast-container');if (!container) {container = document.createElement('div');container.id = 'toast-container';container.className = 'toast-container';document.body.appendChild(container);}return container;}export function showToast(message, opts = {}) {const { timeout = 4000 } = opts;const wrap = getContainer();const toast = document.createElement('div');toast.className = 'toast';toast.setAttribute('role', 'status');toast.setAttribute('aria-live', 'polite');toast.innerHTML = `<span class="toast-message"></span><button class="toast-close" aria-label="Close">&times;</button>`;toast.querySelector('.toast-message').textContent = message;wrap.prepend(toast);const timer = setTimeout(close, timeout);function close() {clearTimeout(timer);toast.remove();}toast.querySelector('.toast-close').addEventListener('click', close);}export default { showToast };